import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate, NavLink, Link } from "react-router-dom";
import cookies from "js-cookie"

const Home = () => {

    const [allGames, setAllGames] = useState([])
    const [firstName, setFirstName] = useState(cookies.get("firstName"))
    const [lastName, setLName] = useState(cookies.get("lastName"))

    const navigate = useNavigate()

    useEffect(()=>{                                 // grabs all Games
        axios.get("http://localhost:8000/allgames")
        .then((res)=>{
            setAllGames(res.data);
	})
        .catch((err)=>{
            console.log(err);
        })
    })

    const logoutHandler = (e) => {
        e.preventDefault()

        axios.get('http://localhost:8000/logout')
            .then((res) => {
                navigate("/login")
            }).catch((err) => {
                console.log(err)
            })
    }

    return(
        <div>
            <div className="nav">
                <h3>Welcome {firstName}!</h3>
                <span className="align-self-center"><NavLink to={'/home'} activeclassname="active">Home</NavLink> | <NavLink to={`/yourgames/${firstName}/${lastName}`}>Your Games</NavLink> | <NavLink to={'/addgame'}>Add New Game</NavLink> | <Link onClick={logoutHandler} >Logout</Link></span>
                <hr className="nav-line mt-3"/>
            </div>
            <div className="main-page-container">
                <h1 className="mb-3">All Games</h1>
                <hr className="home-line" />
                {
                    allGames.map((game,index) => {
                        return (
                            <div key={index}>
                                <div className="row d-flex justify-content-between">
                                    <div className="col-3 game-info">
                                        <h5><NavLink to={`/game/${game._id}`}>{game.gameName}</NavLink></h5>
                                        <h6>Uploaded by: {game.uploadedBy}</h6>
                                        <h6>Uploader Rating: {game.rating}/10</h6>
                                    </div>
                                    <div className="col-8 align-self-center">
                                        <h6>{game.description}</h6>
                                    </div>
                                </div>
                                <hr className="game-line" />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Home;
