import React from "react";
import { useState } from "react";
import { useNavigate, Link } from 'react-router-dom'
import axios from 'axios'

const Login = () => {

    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const navigate = useNavigate("")

    const onLoginHandler = (e) => {
        e.preventDefault()

        axios.post('http://localhost:8000/login', {email, password},{withCredentials:true})
            .then((res) => {
                navigate("/home")
            }).catch((err) => {
                console.log(err)
            })
    }

    return(
        <div>
            <h1 className="title">The Scoop</h1>
            <hr className="hLine" />
            <div className="form-container">
                <div className="row justify-content-center">
                    <div className="col-6">
                        <form onSubmit={onLoginHandler}>
                            <h5>Login</h5>
                            <div className="form-floating mb-3">
                                <input className="form-control" placeholder="email" name="email" value={email} onChange = {(e) => {setEmail(e.target.value)}}/>
                                <label htmlFor="firstName">Email</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input className="form-control" placeholder="password" name="password" value={password} onChange = {(e) => {setPassword(e.target.value)}}/>
                                <label htmlFor="password">Password</label>
                            </div>
                            <button className="btn btn-success" type="button submit">Login</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login
