import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { NavLink, useParams, useNavigate } from "react-router-dom";

const GameDetails = () => {

    const [game, setGame] = useState("")

    const {id} = useParams()
    const navigate = useNavigate()

    useEffect(()=>{
        axios.get(`http://localhost:8000/game/${id}`)
        .then((res)=>{
            setGame(res.data)
        }).catch((err)=>{
            console.log(err)
        })
    },[])

    return(
        <div>
            <div className="nav">
                    <h3>Welcome!</h3>
                    <span className="align-self-center"><NavLink to={'/home'} activeclassname="active">Home</NavLink> | <NavLink to={'/yourgames'}>Your Games</NavLink> | <NavLink to={'/addgame'}>Add New Game</NavLink></span>
                    <hr className="nav-line mt-3"/>
            </div>
            <div>
                <h1>{game.gameName}</h1>
                <hr />
                <h4>Uploader Rating: {game.rating}/10</h4>
                <h6>{game.description}</h6>
                <h6>Other notes: {game.otherNotes}</h6>
            </div>
        </div>
    )

}

export default GameDetails
