import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate, NavLink } from "react-router-dom";
import jwt from "jwt-decode"

const Home = () => {

    const [allGames, setAllGames] = useState([])
    const [userId, setUserId] = useState("")

    const navigate = useNavigate()

    useEffect(()=>{                                 // grabs all our Games
        axios.get("http://localhost:8000/allgames")
        .then((res)=>{
            setAllGames(res.data);
	})
        .catch((err)=>{
            console.log(err);
        })
    })

    useEffect(() => {
        const id = JSON.parse(localStorage.getItem("_id"))
        if(id){
            console.log(id)
        }
    },[])

    return(
        <div>
            <div className="nav">
                <h3>Welcome!</h3>
                <span className="align-self-center"><NavLink to={'/home'} activeclassname="active">Home</NavLink> | <NavLink to={'/yourgames'}>Your Games</NavLink> | <NavLink to={'/addgame'}>Add New Game</NavLink> | <NavLink to={'/logout'}>Logout</NavLink></span>
                <hr className="nav-line mt-3"/>
            </div>
            <div className="main-page-container">
                <h1 className="mb-3">All Games</h1>
                <hr className="home-line" />
                {
                    allGames.map((game,index) => {
                        return (
                            <div>
                                <div className="row d-flex justify-content-between">
                                    <div className="col-3 game-info">
                                        <h5><NavLink to={`/game/${game._id}`}>{game.gameName}</NavLink></h5>
                                        <h6>Uploader Rating: {game.rating}/10</h6>
                                    </div>
                                    <div className="col-9">
                                        <h6>{game.description}</h6>
                                    </div>
                                </div>
                                <hr className="game-line" />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Home;
