import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import cookies from "js-cookie"
import { Link, NavLink, useNavigate } from "react-router-dom";

const YourGames = () => {

    const [firstName, setFName] = useState(cookies.get("firstName"))
    const [lastName, setLName] = useState(cookies.get("lastName"))

    const [games, setGames] = useState([])

    const navigate = useNavigate()

    useEffect(()=>{
        axios.get(`http://localhost:8000/yourgames`, {firstName, lastName})
        .then((res)=>{
            setGames(res.data)
        }).catch((err)=>{
            console.log(err)
        })
    })

    const logoutHandler = (e) => {
        e.preventDefault()

        axios.get('http://localhost:8000/logout')
            .then((res) => {
                navigate("/login")
            }).catch((err) => {
                console.log(err)
            })
    }

    return(
        <div>
            <div className="nav">
                    <h3>Welcome {firstName}!</h3>
                    <span className="align-self-center"><NavLink to={'/home'}>Home</NavLink> | <NavLink to={'/yourgames'} activeclassname="active">Your Games</NavLink> | <NavLink to={'/addgame'}>Add New Game</NavLink> | <Link onClick={logoutHandler}>Logout</Link></span>
                    <hr className="nav-line mt-3"/>
            </div>
            {games.map((game,index) => {
                return(
                    <div key={index}>
                        <div className="row d-flex justify-content-between">
                            <div className="col-3 game-info">
                                <h5><NavLink to={`/game/${game._id}`}>{game.gameName}</NavLink></h5>
                                <h6>Uploaded by: {game.uploadedBy}</h6>
                                <h6>Uploader Rating: {game.rating}/10</h6>
                            </div>
                            <div className="col-8">
                                <h6>{game.description}</h6>
                            </div>
                        </div>
                        <hr className="game-line" />
                    </div>
                )
            })}
        </div>
    )

}

export default YourGames
