import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

const Home = () => {

    const [allGames, setAllGames] = useState([])

    useEffect(()=>{                                 // grabs all our Games
        axios.get("http://localhost:8000/allgames")
        .then((res)=>{
            setAllGames(res.data);
	})
        .catch((err)=>{
            console.log(err);
        })
    })

    return(
        <div>
            <div className="nav">
                <h3>Welcome!</h3>
                <span className="align-self-center"><NavLink to={'/home'} activeclassname="active">Home</NavLink> | <NavLink to={'/yourgames'}>Your Games</NavLink> | <NavLink to={'/addgame'}>Add New Game</NavLink></span>
                <hr className="nav-line mt-3"/>
            </div>
            <div className="main-page-container">
                <h1 className="mb-3">All Games</h1>
                <hr className="home-line" />
                {
                    allGames.map((game,index) => {
                        return (
                            <div className="game-card">
                                <div>
                                    <h5>{game.gameName}</h5>
                                    <h6>Rating: {game.rating}/10</h6>
                                </div>
                                <div>
                                    <p>{game.description}</p>
                                </div>
                            </div>
                        )
                    })
                }
                <hr className="hLine" />
            </div>
        </div>
    )
}

export default Home;
